define("theme/basic.css", ["curl/plugin/style", "require"], function (injector, require) { var text = "html {
	font-size: 100%;
	-webkit-text-size-adjust: 100%;
	-ms-text-size-adjust: 100%;

    font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
    font-weight: lighter;
    color: #222;

    transition: opacity 1s ease;
	-webkit-transition: opacity 1s ease;
	-moz-transition: opacity 1s ease;
	-o-transition: opacity 1s ease;
	-ms-transition: opacity 1s ease;
}

body {
    margin: 0;
    font-size: 1em;
    line-height: 1.4;
}

h1, h2, h3 {
    color: #333;
}

h1 {
    font-weight: bold;
    font-size: 3em;
    letter-spacing: -.1ex;
}

section, footer {
  margin-left: auto;
  margin-right: auto;
}

section.title h1 {
  margin: 0;
}

section.title p {
  margin: 0;
}

p {
  color: #3a3a3a;
}

footer > p {
  margin: 8px 0;
}

section.viz {
  margin: 50px 0;
  padding: 20px 0;
}

section.viz:nth-of-type(even) {
  background: #fafafa;
}

section.viz header, section.title {
  width: 600px;
  margin-left: auto;
  margin-right: auto;
}

section.viz footer {
  width: 800px;
  font-size: 90%;
}

section.viz div.viz {
  width: 800px;
  margin-top: 20px;
  margin-bottom: 20px;
  margin-left: auto;
  margin-right: auto;
}

footer.footer p {
  font-size: 70%;
}

@media (max-width: 800px) {
  section.viz footer {
    width: 600px;
  }

  section.viz div.viz {
    width: 600px;
    margin-top: 15px;
    margin-bottom: 15px;
  }
}

@media (max-width: 600px) {
  section.viz header, section.viz footer, section.title {
    width: 400px;
  }

  section.viz footer {
    width: 400px;
  }

  section.viz div.viz {
    width: 400px;
    margin-top: 10px;
    margin-bottom: 10px;
  }
}



